import React, { useEffect, useState } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.css';


const Books = () => {
    const [books, setBooks] = useState([])
    useEffect (() => {
        const fetchAllBooks = async () => {
            try {
                const res = await axios.get("http://localhost:8800/books");
                setBooks(res.data);
                console.log(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchAllBooks();
    }, []);
    // console.log(books);
    return (
        <div>
            <h1>Book Shop</h1>
            <div className="container">
                <div className="books row">
                    {books.map((book) => (
                        <div key={book.id} className="book col-3">
                            <img src={book.image} alt="" className="w-100"/>
                            <h2>{book.title}</h2>
                            <p>{book.description}</p>
                            <p>{book.price} VND</p>
                            <button className="update">
                                <Link to={`/update/${book.id}`} style={{ color: "inherit", textDecoration: "none" }}>
                                    Update
                                </Link>
                            </button>
                            <button className="delete mx-2">Delete</button>
                        </div>
                    ))}
                </div>
            </div>
            <button className="addHome m-3">
                <Link to="/add" style={{ color: "inherit", textDecoration: "none" }}> Add new book </Link>
            </button>
        </div>
    );
}

export default Books
