import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

const Update = () => {

    const location = useLocation();
    const navigate = useNavigate();
    const bookId = location.pathname.split("/")[2];

    const [book, setBook] = useState([])
    useEffect (() => {
        const fetchBook = async () => {
            try {
                const res = await axios.get("http://localhost:8800/book/"+bookId);
                setBook(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchBook();
    }, []);
    const [error,setError] = useState(false)

    const handleChange = (e) => {
        setBook((prev) => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8800/books/${bookId}`, book);
            navigate("/");
        } catch (err) {
            console.log(err);
            setError(true);
        }
    };

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-8">
                    <div className="form">
                        <h1>Edit Book</h1>
                        <div className="mb-3">
                            <label htmlFor="" className="form-label w-100 text-start">Title</label>
                            <input className="w-100 mb-2" type="text" placeholder="Title" name="title" onChange={handleChange}/>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="" className="form-label w-100 text-start">Description</label>
                            <textarea className="w-100 mb-2" rows={5} type="text" placeholder="Description" name="description" onChange={handleChange} value={book['0']?.description}/>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="" className="form-label w-100 text-start">Price</label>
                            <input className="w-100 mb-2" type="number" placeholder="Price" name="price" onChange={handleChange} value={book['0']?.price}/>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="" className="form-label w-100 text-start">Image</label>
                            <input className="w-100 mb-2" type="text" placeholder="Image" name="image" onChange={handleChange} value={book['0']?.image}/>
                            <img src={book['0']?.image} alt="" />
                        </div>
                        <div className="d-flex justify-content-start">
                            <button onClick={handleClick} className="btn btn-outline-success mx-3">Edit</button>
                                {error && "Something went wrong!"}
                            <Link className="btn btn-outline-primary" to="/">Cancel</Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Update;
